import React, { useEffect, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

import './App.css'; // Include your CSS file if you have one

const App = () => {
  const [selectedPoint, setSelectedPoint] = useState(null);
  const [bufferDistance, setBufferDistance] = useState(0);
  const [fetchedData, setFetchedData] = useState(null);

  useEffect(() => {
    // Setting up the map
    const map = L.map('map').setView([43.70, -79.42], 10);

    // Adding OpenStreetMap tile layer
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      maxZoom: 19,
      attribution: '&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a>',
    }).addTo(map);

    // Adding a black dot marker for the selected point (if any)
    if (selectedPoint) {
      L.circleMarker(selectedPoint, {
        radius: 5,
        color: 'black',
        fillOpacity: 1,
      }).addTo(map);

      // Adding a circle with the specified radius in meters
      L.circle(selectedPoint, {
        radius: bufferDistance * 1000, // Convert bufferDistance to meters
        color: 'blue',
        fillOpacity: 0.2,
      }).addTo(map);
    }

    // Handling map click event
    const onMapClick = (e) => {
      setSelectedPoint([e.latlng.lat, e.latlng.lng]);
    };

    map.on('click', onMapClick);

    // Clean up on component unmount
    return () => {
      map.remove();
    };
  }, [selectedPoint, bufferDistance]);

  const handleBufferDistanceChange = async (e) => {
    const newBufferDistance = e.target.value;
    setBufferDistance(newBufferDistance);

    // Making a request to the backend when buffer distance changes
    try {
      //const response = await fetch(`http://localhost:4000/fetch?lat=${selectedPoint[1]}&lng=${selectedPoint[0]}&radius=${newBufferDistance}`);
      const response = await fetch(`http://localhost:4000/fetch?lat=${selectedPoint[1]}&lng=${selectedPoint[0]}&radius=${newBufferDistance}`);
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      console.log("This is fetched Data", data )
      setFetchedData(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <div>
      <div id="map" className="map"></div>
      <div>
        <label>Select a Point:</label>
        {selectedPoint && <span> Latitude: {selectedPoint[0]}, Longitude: {selectedPoint[1]}</span>}
      </div>
      <div>
        <label>Buffer Distance (in kilometers):</label>
        <input type="number" value={bufferDistance} onChange={handleBufferDistanceChange} />
      </div>
      <div>
        <h2>Fetched Data:</h2>
        {fetchedData ? (
          <div>
            <p>Total Population: {fetchedData.total_population}</p>
            <p>Average Salary: {fetchedData.average_salary}</p>
            {/* Add more lines for each property you want to display */}
          </div>
        ) : (
          <p>No data fetched yet.</p>
        )}
      </div>
    </div>
  );
};

export default App;
